<?phpclass Bizspice_Bannerslider_Block_Bannerslider extends Mage_Core_Block_Template{	public function getslides()	{		$slidecollection=Mage::getModel('bannerslider/bannerslider')->getCollection()->addFieldToFilter('status', 1);				return $slidecollection;		}		public function pagecontent_toHtml($data)    {        /* @var $helper Mage_Cms_Helper_Data */        $helper = Mage::helper('cms');        $processor = $helper->getPageTemplateProcessor();        $html = $processor->filter($data);        $html = $this->getMessagesBlock()->toHtml() . $html;        return $html;    }		}